import React from "react";

const InitialValue = React.createContext({
  counterValue: 0,
  lastValue: "",
  setValue: () => {},
});

export const InitialValueProvider = (props) => {
  return (
    <InitialValue.Provider value={{ counterValue: 0 }}>
      {props.children}
    </InitialValue.Provider>
  );
};

export default InitialValue;
